const wordmanager = require('wordmanager.js'); // this is my other package to analyze words

/**
 * @param {string} word put the word here
 * @returns {string} the result is a scrambled word
 * @description Scramble a word or a string
 */

module.exports = function (word){
    if(typeof word !== 'string') throw new Error('Error: No String Detected!');
    if(word.length === 0) throw new Error('Error: The Given String must be at least 1 character');

    var arr = word.split('');      
    arr.sort(function() {
      return 0.5 - Math.random();
    });  
    word = arr.join('');                
    return word;    
}

/**
 * @param {string} word1 put the word here
 * @returns {string} the result is a scrambled test
 * @description Scramble a text
 */

module.exports.scrambletext = function (word1){
    if(typeof word1 !== 'string') throw new Error('Error: No String Detected!');

    var word2 = wordmanager.getWord(word1);

    if(word2.words <= 1) throw new Error('Error: The Given string must be at least 1 word');

    var shuffled = word1.split(/\s\b(?!\s)/).sort(function(){return 0.5-Math.random()}).join(' ');

    return shuffled;
}